/*
 * Tabs for navigation. Return jquery object for chaining.
 * author: suqing
 * date  : 2011.09.08
 */
$.fn.tab = function() {
    return this.each(function(k, v) {
        $(v).bind('click', function(e) {
            var t = e.target,
                $t = $(t);
            switch (t.tagName) {
                case 'A':
                    $t = $t.parent();
                case 'LI':
                    var $s = $t.siblings('.ft-tab');
                    $t.addClass('selected');
                    $s.removeClass('selected');

                    $('#' + $t.attr('id').replace('tab', 'content')).show();
                    $s.each(function(k, v) {
                        $('#' + $(v).attr('id').replace('tab', 'content')).hide();
                    });
                    break;
            }
            $.Kelude.FIT_PARENT();
        })
    })
};

/*
 * Simple Tabs
 * author: suqing
 * date  : 2011.09.09
 */
$.fn.panel_tab = function() {
    return this.each(function(k, v) {
        $(v).bind('click', function(e) {
            var t = e.target,
                $t = $(t);
            if (t.tagName == 'LI') {
                var $s = $t.siblings('li');
                $('.' + $t.attr('class').replace('tab', 'content')).css('display', 'block');
                $s.each(function(k, v) {
                    $('.' + $(v).attr('class').replace(' selected', '').replace('tab', 'content')).hide();
                });
                $s.removeClass('selected');
                $t.addClass('selected');
            }
        })
    })
};

